import React from 'react'

import Card from '@mui/material/Card';
// import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { CardActionArea, TextField } from '@mui/material';

import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

import blockdata100 from '../../utils/blockdata100.js'
import { Prev } from 'react-bootstrap/esm/PageItem';

const Blockcard = (id) => {

  const [open, setOpen] = React.useState(false);
  // const [getEmail, setGetEmail] = React.useState('');
  const [cardData, setCardData] = React.useState({
    blocknumber: id,
    userStatus: "Open",
    userFirstName: "",
    userLastName: "",
    userEmail: ""
  })

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    console.log(cardData)
  };

  const handleInputChange = (e) => {
    setCardData({...cardData,
      [e.target.name]: e.target.value
    })
      console.log(cardData);

  }

  const handleStatusCancel = () => {
    setCardData({
      blocknumber: id,
      userStatus: "Open",
      userFirstName: "",
      userLastName: "",
      userEmail: ""
    })
    handleClose();
    console.log(cardData)
  }

  // const claimBlock = () => {

  //   blockdata100.blocks[id-1].email=getEmail

  //   handleClose()
  // }

  return (
    <div className="Blockcard">

    <Card>
      <CardActionArea onClick={handleClickOpen}>
      <CardContent>
        <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
          { cardData.userEmail !== "" ? cardData.userEmail : cardData.userStatus + " " + cardData.blocknumber }
        </Typography>
      </CardContent>
      </CardActionArea>
    </Card>

    <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To claim BLOCK before anyone else, please enter your information and click "Claim Block"
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="emailaddress"
            label="Email Address"
            type="text"
            name="userEmail"
            onChange={handleInputChange}
            fullWidth
            variant="standard"
          />
          <TextField
            autoFocus
            margin="dense"
            id="userFirstName"
            label="First Name"
            type="text"
            name="userFirstName"
            onChange={handleInputChange}
            fullWidth
            variant="standard"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleStatusCancel}>Cancel</Button>
          <Button onClick={handleClose}>Claim Block</Button>
        </DialogActions>
      </Dialog>

    </div>
  )
}

export default Blockcard
