import React from 'react'
import Grid from '@mui/material/Grid';
// import { Paper } from '@mui/material';
// import Blockcard from '../blockcard/Blockcard';

import Card from '@mui/material/Card';
// import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { CardActionArea, TextField } from '@mui/material';

import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

import blockdata100 from "../../utils/blockdata100.js"

const Blockpool = () => {

  const [blockPoolData, setBlockPoolData] = React.useState(blockdata100)
  const [open, setOpen] = React.useState(false);
  const [getEmail, setGetEmail] = React.useState('');

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleTextUpdate = (e) => {
    setGetEmail(e.target.value)
    console.log(getEmail)
  }

  // const claimBlock = () => {

  //   blockdata100.blocks[id-1].email=getEmail

  //   handleClose()
  // }

  const BlockCard = ({id, status, firstName, lastName}) => {
    <div className="Blockcard">

    <Card>
      <CardActionArea onClick={handleClickOpen}>
      <CardContent>
        <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
          { status === "Open" ? status + id: firstName + " " + lastName }
        </Typography>
      </CardContent>
      </CardActionArea>
    </Card>

    <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To claim BLOCK {id} before anyone else, please enter your information and click "Claim Block"
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="emailaddress"
            label="Email Address"
            type="text"
            value={getEmail}
            onChange={handleTextUpdate}
            fullWidth
            variant="standard"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleClose}>Claim Block</Button>
        </DialogActions>
      </Dialog>

    </div>
  }

  return (
    <div className="Blockpool">

    <Grid container spacing={3} columns={10}>

        {blockdata100.blocks.map((block) => (
            <Grid item xs={1}>
                <BlockCard id={block.blocknumber} status={block.status} firstName={block.firstName} lastName={block.lastName}/>
            </Grid>
            ))}

    </Grid>

    </div>
  )
}

export default Blockpool
